import EventEmitter from"../event/EventUtils";class PdfState extends EventEmitter{constructor(){super(),this._scale=1,this._isLoading=!0,this._currentPage=1,this._containerId=""}static getInstance(t){var e;if(t)return this.pdfStates.has(t)?this.pdfStates.get(t):(e=new PdfState,this.pdfStates.set(t,e),e);throw new Error("Invalid ID: ID cannot be null or empty.")}static removeInstance(t){this.pdfStates.has(t)?this.pdfStates.delete(t):console.warn(`PDF State with ID "${t}" does not exist.`)}static listInstances(){return Array.from(this.pdfStates.keys())}get scale(){return this._scale}set scale(t){this._scale!==t&&(this._scale=t)}get pdfInstance(){return this._pdfInstance}set pdfInstance(t){this._pdfInstance!==t&&(this._pdfInstance=t,this.emit("pdfInstanceChange",t))}get isLoading(){return this._isLoading}set isLoading(t){this._isLoading!==t&&(this._isLoading=t,this.emit("loadingChange",t))}get currentPage(){return this._currentPage}set currentPage(t){this._currentPage!==t&&(this._currentPage=t)}get containerId(){return this._containerId}set containerId(t){this._containerId!==t&&(this._containerId=t)}get uiLoading(){return this._uiLoading}set uiLoading(t){this._uiLoading!==t&&(this._uiLoading=t)}}PdfState.pdfStates=new Map;export default PdfState;