class EventEmitter{constructor(){this.events={}}on(e,t){this.events[e]||(this.events[e]=[]),this.events[e].push(t)}emit(e,...t){this.events[e]&&this.events[e].forEach(e=>e(...t))}off(e,t){this.events[e]&&(this.events[e]=this.events[e].filter(e=>e!==t))}removeAllListeners(e){e?delete this.events[e]:this.events={}}}export default EventEmitter;